/*  Liệt kê tất cả các ước số lẻ của số nguyên dương n do
    người dùng nhập từ bàn phím.                                                         */#include <iostream>using namespace std;int main() {    int n, tong = 0, sole;    //INPUT NHAP SO NGUYEN N    do {        cout << "Nhap gia tri cua so nguyen duong n : \n";        cin >> n;        if (n <= 0)            cout << "Nhap sai so nguyen duong,vui long nhhap lai \n";    } while (n <= 0);    //PROCESSING    for (int i = 1; i <= n; i++) {        if (n % 2 != 0) {            if (n % i == 0) {                sole = i;            }        }        else            cout << "Khong phai uoc so \n";    }    //OUTPUT SO UOC LE CUA SO NGUYEN N    cout << "Cac uoc le cua so nguyen " << n << " la :" << sole << endl;    system("pause");    return 1;}