/*Viết chương trình nhập vào 3 cạnh của 1 tam giác và xuất ra màn hình cho biết tam giác đó là tam giác gì?*/#include <iostream>using namespace std;int main() {	int a, b, c;	//NHap vao 3 canh cua tam giac	do {		cout << "Nhap ba canh cua mot tam giac : ";		cin >> a, b, c;		if (a && b && c <= 0)			cout << "Nhap ba canh cua mot tam giac sai roi  \n";	} while (a&&b&&c <= 0);	if (a + b > c || a + c > b || b + c > a)		cout << "Day la tam giac thuong \n";	else if (a == b || a == c || b == c)		cout << "Day la tam giac can \n";	else if((a==b)&&(b==c))		cout << "Day la tam giac deu \n";	else if(a * a == b * b + c * c || b * b == a * a + c * c || c * c == b * b + a * a)		cout << "Day la tam giac vuong \n";	else if((a == b || a == c || b == c)&&(a * a == b * b + c * c ))		cout << "Day la tam giac vuong can \n";	else 		cout << "Khong phai tam giac \n";	system("pause");	return 1;}